mapper.select:
select product_id
from pro_infoManage
where product_code = #{product_code}

select * 
from pro_infoManage

	status = 0 and product_check = #{product_check} and product_id != #{product_id}

			and product_chnpy like #{product_chnpy}


select top ${bean.rows}*


select *

from pro_infoManage

	status = 0

		and product_code like #{bean.product_code}


		and product_code like #{code}


			and product_check = #{bean.product_check}


			and product_chnpy like #{bean.product_chnpy}


		and product_name like #{bean.product_name}


		and product_medinsuvariety = #{bean.product_medinsuvariety}



			and product_dsurveillanceid is null


			and product_dsurveillanceid >0



		and product_immaintain = #{bean.product_immaintain}


		and product_id not in(select top ${bean.offset} product_id from pro_infoManage where status = 0

			and product_code like #{bean.product_code}


			and product_code like #{code}


			and product_check = #{bean.product_check}


			and product_chnpy like #{bean.product_chnpy}


			and product_name like #{bean.product_name}


			and product_medinsuvariety = #{bean.product_medinsuvariety}



				and product_dsurveillanceid is null


				and product_dsurveillanceid >0



			and product_immaintain = #{bean.product_immaintain}


			order by ${bean.sort} ${bean.order}

	)



	order by ${bean.sort} ${bean.order}

select count(product_id) as count 
from pro_infoManage

	status = 0

		and product_code like #{product_code}


		and product_check = #{product_check}


		and product_dsurveillanceid = #{product_dsurveillanceid}

select * from pro_infoManage where not exists 
	(select product_id from  pmn_rule_product 
	where pro_infoManage.product_id = pmn_rule_product.product_id 
	and pmn_rule_product.rule_id = #{rule_id} 
	and pmn_rule_product.status = 0)
	and pro_infoManage.product_check = 0
	and pro_infoManage.status = 0


		and product_code like #{bean.product_code}


			and product_chnpy like #{bean.product_chnpy}


		and product_name like #{bean.product_name}
